PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "current_team_id" integer, "profile_photo_path" varchar, "created_at" datetime, "updated_at" datetime, "two_factor_secret" text, "two_factor_recovery_codes" text, "two_factor_confirmed_at" datetime, "type" integer not null default '0');
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "companies" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" varchar, "slug" varchar not null, "website" varchar, "logo" varchar, "email" varchar, "status" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "created_at" datetime, "updated_at" datetime, "icon" text);
CREATE TABLE IF NOT EXISTS "tenders" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text not null, "details" text not null, "slug" varchar not null, "company_id" integer not null, "location" varchar not null, "deadline" date not null, "status" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("company_id") references "companies"("id"));
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "bookmarks" ("id" integer primary key autoincrement not null, "user_id" integer not null, "bookmarkable_id" integer not null, "bookmarkable_type" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "category_training" ("id" integer primary key autoincrement not null, "training_id" integer not null, "category_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("training_id") references "trainings"("id") on delete cascade, foreign key("category_id") references "categories"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "category_job" ("id" integer primary key autoincrement not null, "job_id" integer not null, "category_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("job_id") references "job"("id") on delete cascade, foreign key("category_id") references "categories"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "bugs" ("id" integer primary key autoincrement not null, "first_name" varchar not null, "last_name" varchar not null, "email" varchar not null, "type" integer not null, "message" text not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "banners" ("id" integer primary key autoincrement not null, "name" varchar not null, "image" varchar not null, "url" varchar, "status" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "contacts" ("id" integer primary key autoincrement not null, "first_name" varchar not null, "last_name" varchar not null, "phone" integer not null, "email" varchar not null, "type" integer not null, "message" text not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "trainings" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text not null, "details" text not null, "slug" varchar not null, "company_id" integer not null, "category_id" varchar not null, "location" varchar not null, "deadline" date not null, "link" text, "status" integer not null default ('0'), "created_at" datetime, "updated_at" datetime, "added_by" integer, foreign key("company_id") references companies("id") on delete no action on update no action, foreign key("added_by") references "users"("id"));
CREATE TABLE IF NOT EXISTS "job" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "details" text not null, "slug" varchar not null, "company_id" integer not null, "category_id" varchar not null, "location" varchar not null, "how_to_apply" varchar not null, "deadline" date not null, "link" text, "status" integer not null default ('0'), "created_at" datetime, "updated_at" datetime, "added_by" integer, foreign key("company_id") references companies("id") on delete no action on update no action, foreign key("added_by") references "users"("id"));
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "companies_name_unique" on "companies" ("name");
CREATE UNIQUE INDEX "companies_slug_unique" on "companies" ("slug");
CREATE UNIQUE INDEX "companies_website_unique" on "companies" ("website");
CREATE UNIQUE INDEX "companies_email_unique" on "companies" ("email");
CREATE UNIQUE INDEX "categories_name_unique" on "categories" ("name");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE UNIQUE INDEX "bookmarks_user_id_bookmarkable_id_bookmarkable_type_unique" on "bookmarks" ("user_id", "bookmarkable_id", "bookmarkable_type");
CREATE UNIQUE INDEX "trainings_slug_unique" on "trainings" ("slug");
CREATE UNIQUE INDEX "job_slug_unique" on "job" ("slug");
COMMIT;
